import * as React from 'react';
import ShiftsDataGrid from "./ShiftsDataGrid";
import { styled } from "@mui/material/styles";
import { 
        DialogContentText, 
        DialogActions, 
        DialogTitle, 
        DialogContent, 
        Dialog, 
        Box, 
        Paper, 
        Grid, 
        Button
    } from "@mui/material";
// import SelectShiftControl from './SelectShiftControl';
// import SelectNurseControl from "./SelectNurseControl";
import SelectControl from './SelectControl';


const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));


export default function ShiftsListingPage() {
 const [open, setOpen] = React.useState(false);

 const handleClickOpen = () => {
   setOpen(true);
 };

const handleClose = () => {
     setOpen(false);
};

  return (
    <div>
      <Box sx={{ flexGrow: 1 }}>
        <Grid container direction="column">
          <Grid>
            <Item>
              <Button variant="outlined" onClick={handleClickOpen}>
                Set Shift Assignment
              </Button>
            </Item>
            <Item>
              <ShiftsDataGrid></ShiftsDataGrid>
            </Item>
          </Grid>
        </Grid>
      </Box>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Set Shift Assignment</DialogTitle>
        <DialogContent>
          <Grid container direction="column">
            <Grid>
              <Item>
                <SelectControl label="Select Nurse"></SelectControl>
              </Item>
              <Item>
                <SelectControl label="Select Shift"></SelectControl>
              </Item>
            </Grid>
          </Grid>
          <DialogContentText>
            Enter the shift assignment details
          </DialogContentText>

          {/* <SelectNurseControl></SelectNurseControl>
          <SelectShiftControl></SelectShiftControl> */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Submit</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
