import * as React from 'react';
import { styled } from "@mui/material/styles";
import { 
        DialogContentText, 
        DialogActions, 
        DialogTitle, 
        DialogContent, 
        Dialog, 
        Box, 
        Paper, 
        Grid, 
        Button
    } from "@mui/material";
import NurseSelectDropDown from "./NurseSelectDropDown";
import ShiftSelectDropDown from "./ShiftSelectDropDown";
import ShiftsListControl from './ShiftsListControl';


const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));


export default function ShiftsListingPage() {
 const [open, setOpen] = React.useState(false);
 const [isSaveEnabled, setIsSaveEnabled ] = React.useState(false);
 const [shiftSelected, setShiftSelected] = React.useState(0);
 const [nurseSelected, setNurseSelected] = React.useState(0);

 React.useEffect(() => {
   setIsSaveEnabled(shiftSelected !== 0 && nurseSelected !== 0);
 }, [isSaveEnabled]);

 const nurseSelectedHandler  = (selected) => {
  setNurseSelected(selected);
 }

 const shiftSelectedHandler = (selected) => {
   setShiftSelected(selected);
 };

 const handleClickOpen = () => {
   setOpen(true);
 };

const handleClose = () => {
     setOpen(false);
};

const handleSubmit = () => {
};

  return (
    <div>
      <Box sx={{ flexGrow: 1 }}>
        <Grid container direction="column">
          <Grid>
            <Item>
              <Button variant="outlined" onClick={handleClickOpen}>
                Set Shift Assignment
              </Button>
            </Item>
            <Item>
              <ShiftsListControl></ShiftsListControl>
            </Item>
          </Grid>
        </Grid>
      </Box>
      <Dialog fullWidth open={open} onClose={handleClose}>
        <DialogTitle>Set Shift Assignment</DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ padding: "20px" }}>
            Enter the shift assignment details
          </DialogContentText>
          <Box sx={{ padding: "10px" }}>
            <Grid container direction="column" spacing={3}>
              <Grid>
                <Item>
                  <ShiftSelectDropDown
                    shiftSelectedHandler={shiftSelectedHandler}
                  ></ShiftSelectDropDown>
                </Item>
                <Item>
                  <NurseSelectDropDown
                    nurseSelectedHandler={nurseSelectedHandler}
                  ></NurseSelectDropDown>
                </Item>
              </Grid>
            </Grid>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button
            color="primary"
            variant="contained"
            onClick={handleSubmit}
            disabled={isSaveEnabled}
          >
            Save Assignment
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
