import * as React from "react";
// import Box from "@mui/material/Box";
// import InputLabel from "@mui/material/InputLabel";
// import MenuItem from "@mui/material/MenuItem";
// import FormControl from "@mui/material/FormControl";
// import Select from "@mui/material/Select";


export const CustomDropDown = (props) => (
  <div className="form-group">
    <select
      className="form-control"
      name={props.name}
      onChange={props.onChange}
    >
      <option defaultValue>Select {props.name}</option>
      {props.options.map((item, index) => (
        <option key={index} value={item.id}>
          `{item.name} {item.start} - {item.end} ({item.qual_required})`
        </option>
      ))}
    </select>
  </div>
);

export default class CustomListDropDown extends React.Component {
  constructor() {
    super();
    this.state = {
      shifts: [],
      value: "",
    };
  }
  componentDidMount() {
    fetch("http://localhost:9001/shifts", {
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((response) => response.json())
      .then((res) => this.setState({ shifts : res }))
      .catch((reason) => console.log(reason));
  }

  onChange = (event) => {
    this.setState({ value: event.target.value });
  };
  render() {
    return (
      <div>
        <h2>Select Shift</h2>
        <CustomDropDown
          name="Shift"
          options={this.state.shifts}
          onChange={this.onChange}
        />
      </div>
    );
  }
}

