import * as React from "react";
import Box from "@mui/material/Box";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

export default class ShiftSelectDropDown extends React.Component {
  constructor() {
    super();
    this.state = {
      shifts: [],
      value: "",
    };
  }
  componentDidMount() {
    fetch("http://localhost:9001/shifts", {
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((response) => response.json())
      .then((res) => this.setState({ shifts : res }))
      .catch((reason) => console.log(reason));
  }

  onChange = (event) => {
    this.setState({ value: event.target.value });
  };

  render() {
    return (
      <div>
        <Box sx={{ minWidth: 120 }}>
          <FormControl fullWidth margin="dense">
            <InputLabel id="input-label">Select Shift</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={10}
              label="select-shift-label">
            <MenuItem defaultValue>Select Shift</MenuItem>
            { this.state.shifts.map((item, index) => (
              <MenuItem key={index} value={item.id}>
              {item.name} {item.start}{item.end} ({item.qual_required})
              </MenuItem>
            ))}
            </Select>
          </FormControl>
        </Box>
      </div>
    );
  }
}
