import * as React from "react";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
import NurseSelectDropDown from "./NurseSelectDropDown";

const columns = [
  { id: "name", label: "Shift", minWidth: 100 },
  { id: "start", label: "Start Date", minWidth: 170 },
  {
    id: "end",
    label: "End Date",
    minWidth: 170,
  },
  {
    id: "qual_required",
    label: "Qualification Required",
    minWidth: 170,
  },
  {
    id: "nurse_id",
    label: "Nurse Assigned",
    minWidth: 170,
  },
];

export default function ShiftsListControl() {
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [shiftsList, setShiftsList] = React.useState([]);
  const [nurseList, setNurseList] = React.useState([]);

  React.useEffect(()=> {
    fetchShifts();
  }, []);

    React.useEffect(() => {
      fetchNurses();
    }, []);

  const fetchShifts = () => {
        fetch("http://localhost:9001/shifts", {
          mode: "cors",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        })
        .then((response) => response.json())
        .then((res) => setShiftsList(res))
        .catch((reason) => console.log(reason));
  }

  const fetchNurses = () => {
    fetch("http://localhost:9001/nurses")
    .then((res) => res.json())
    .then((res) => setNurseList(res))
    .catch((error) => console)
  }

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  return (
    <Paper sx={{ width: "100%", overflow: "hidden" }}>
      <TableContainer sx={{ maxHeight: 440 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {shiftsList.map((shift) => {
              const nurse = nurseList.find((nurse) => nurse.id === shift.nurse_id);
              return {
              ...shift,
              "nurse":  `${nurse.first_name}  ${nurse.last_name}`
              }
            })
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((shift) => {
                return (
                  <TableRow hover role="checkbox" tabIndex={-1} key={shift.id}>
                    {columns.map((column) => {
                      const value = shift[column.id];
                      return (
                        <TableCell key={column.id} align={column.align}>
                          {column.format && typeof value === "number"
                            ? column.format(value)
                            : value}
                        </TableCell>
                      );
                    })}
                  </TableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={shiftsList.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
